func convert(str string) int {
	count1 := 1
	count2 := 1
	count3 := 0
	ind := 0

	for i, char := range str {
		if char == '(' {
			count1++
		} else if char == ')' {
			count2++
		}
		count3 = count1 - count2
		if count3 == -1 {
			ind += i
			ind = ind + 1
			return ind
		}
	}
	return ind
}
